import React, { useEffect } from 'react'
import { useState } from 'react';
import ImageSlider from './SlideBar.js'
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";
export default function Products() {

      const [ data, setdata]= useState([])
      const fun = async()=>{
        let xhttp = await fetch("https://fakestoreapi.com/users");
        let data = await xhttp.jason()    
        setdata(data)       
      }
     
       useEffect=()=>{
        fun()
       }
  return (
    <div className='container product'>
      <h1 className='text-dark m-3'>Search by catagories:</h1> 
        <ImageSlider/>
        {
          data.map((e, index) => (
            true ? (
              <div className="mapped mx-3 col-lg-4 border border-2 p-3" key={index}>
                <img className="mx-auto" src={e.username} style={{ 'width': '100%', 'height': '130px' }}></img>
                <p><label style={stlLable}>Price :</label> {e.email}</p>
                <p><label>Category :</label> {e.passw}</p>
                <button type='button' className='p-1 bg-warning'>Add to cart</button>
              </div>
            ) : null
          ))
        }
    </div>
  )
}
